data:
a{color:#0F5D9B;text-decoration:none!important;}
blockquote{border-color:#ECD53C;}
body{font-family:'Roboto Condensed',Helvetica,Arial,Lucida,sans-serif !important;font-size:14px;}

/* Button */
.btn-bbg,.btn-bbg:hover{text-shadow:none!important}
.btn-bbg{background-color:#ecd53c;border-width:0!important;border-color:rgba(255,255,255,0);border-radius:0;color:#0f5d9b;display:block!important;font-size:13px;font-weight:600!important;line-height:1.7em!important;margin:5px;padding:3px 10px;text-align:center;text-decoration:none;transition:background-color .3s,border .3s;transform:none;-webkit-transform:none}
.btn-bbg:hover{background-color:orange!important;color:#fff;top:0;right:0;bottom:0;left:0;transform:none;-webkit-transform:none}
.btn-bbg:after{display:none}

/* Loader */
.ubbg-loader{position:fixed;z-index:99999999999999;top:0;left:0;width:100%;height:100%;background:white;display:flex;justify-content:center;align-items:center;}
.ubbg-loader > img{width:47px;}
.ubbg-loader.hidden{animation:ubbgFadeOut .05s;animation-fill-mode:forwards;}
@keyframes ubbgFadeOut{100%{opacity:0;visibility:hidden;}}
.ubbg-thumb{height:100px;border:1px solid black;margin:10px;}

/* Floating Button */
#pojo-a11y-toolbar .pojo-a11y-toolbar-toggle a{position:fixed;left:20px;bottom:20px;background-color:#083d62!important;color:#fff!important;border-radius:50%;padding:20px 18px 20px 22px!important;-webkit-box-shadow:0px 2px 10px rgba(0,0,0,0.2)!important;box-shadow:0px 2px 10px rgba(0,0,0,0.2)!important;}
#pojo-a11y-toolbar .pojo-a11y-toolbar-toggle svg{max-width:20px;}
#pojo-a11y-toolbar .pojo-a11y-toolbar-overlay {background-color:#fff!important;border:0px!important;-webkit-box-shadow:0px 2px 10px rgba(0,0,0,0.2);box-shadow:0px 2px 10px rgba(0,0,0,0.2);}
#pojo-a11y-toolbar .pojo-a11y-toolbar-overlay .pojo-a11y-toolbar-inner .pojo-a11y-toolbar-title{text-align:center;text-transform:uppercase;color:#083D62;}
#pojo-a11y-toolbar .pojo-a11y-toolbar-overlay .pojo-a11y-toolbar-inner .pojo-a11y-toolbar-items li.pojo-a11y-toolbar-item a {display:block;padding:9px 6px 9px 11px;font-size:14px;line-height:1;color:#0F5D9B;text-decoration:none !important;display:flex;justify-content:space-between;flex-direction:row-reverse;}
#help-center{position:fixed;right:20px;bottom:20px;z-index:9999;text-align:right;transition:bottom 0.3s;width:60px;height:60px;border-radius:50%;-webkit-box-shadow:0px 2px 10px rgba(0,0,0,0.2);box-shadow:0px 2px 10px rgba(0,0,0,0.2);display:inline-flex;align-items:center;font-weight:700;font-size:16px;line-height:140%;padding:9px 21px;justify-content:center;color:#0A528B;background-color:#ECD53C;}

/* Blink */
.blink{animation:blink-animation 1s steps(5, start) infinite;-webkit-animation:blink-animation 1s steps(5, start) infinite;}
@keyframes blink-animation {to {visibility:hidden;}}
@-webkit-keyframes blink-animation {to {visibility: hidden;}}

/* Header */
.nav>li>a{padding:0 0 8px 0!important;}
.gtranslate_wrapper{padding:5px 0 7px 0;}
.gt_switcher .gt_selected{background:#2E2A93!important;}
.gt_switcher .gt_selected a{padding:0px 5px;padding:2px 5px!important;}
.gt_switcher a, .gt_switcher .gt_selected a{border:none!important;font-size:10pt!important;font-family:'Roboto Condensed',Helvetica,Arial,Lucida,sans-serif;font-weight:400;}
.gt_switcher a img{border-radius:50px;object-fit:cover;width:20px!important;height:20px!important;margin:0px 3px!important;}
.gt_switcher .gt_selected a:after{height:20px!important;}
a.glink{padding:0px 0 0 13px;}
a.glink img{width:16px;opacity:1!important;}
a.glink img:hover{opacity:0.8!important;}
a.glink span{color:#FFF;font-size:13px!important;font-weight:500;margin-right:0!important;vertical-align:middle!important;}
a.glink:hover,a.glink span:hover{color:#ECD53C;}
.et-db #et-boc .et-l .et_pb_section{padding:0;}
.et-db #et-boc .et-l .et_pb_row_0_tb_header, .et-db #et-boc .et-l .et_pb_row_1_tb_header{width:100%;}
#header-row-topnav{}
#header-row-mainmenu{}
#header-logo{box-shadow:0px 12px 18px -6px rgba(0,0,0,.3);float:left;}
@media only screen and (max-width:980px){
	#header-logo{left:20%;}
	#header-logo img{height:39px;}
}
#header-medsos p.medsos-wrapper{margin:0;font-weight:400;padding:6px 7px;}
#header-medsos p.medsos-wrapper a{color:#FFF;padding:0 3px;}
#header-medsos p.medsos-wrapper a:hover{color:#ECD53C;}
#et-boc .et-l.et-l--header .et_builder_inner_content.et_pb_gutters3{height:auto!important;}
.et-menu .menu-item-has-children>a:first-child{padding-right:20px!important;}
.et_mobile_nav_menu{background:#CE6900;border-radius:4px;}
.et_pb_menu_0_tb_header.et_pb_menu .nav li ul, .et_mobile_menu{max-height:500px;overflow-y:auto;overflow-x:hidden;}
.et_pb_menu_0_tb_header.et_pb_menu ul li a:hover{background:none;}
.et-menu li li a{padding:7px;width:200px;line-height:22px;}
.et_pb_menu_0_tb_header.et_pb_menu .et-menu-nav li a:hover{border:none!important;}
.et_pb_menu_0_tb_header .et_pb_menu__icon.et_pb_menu__search-button, .et_pb_menu_0_tb_header .et_pb_menu__icon.et_pb_menu__close-search-button{margin:0 11px 0 22px;}
.et_pb_menu .et_pb_menu__search-container{max-width:840px;}
.et_pb_fullwidth_menu .et_pb_menu__search-input, .et_pb_menu .et_pb_menu__search-input, .et_pb_fullwidth_menu .et_pb_menu__search-input::placeholder, .et_pb_menu .et_pb_menu__search-input::placeholder{color:#fff;}

/* Footer */
footer a{color:#ECD53C;}
footer a:hover{color:orange;}
footer h2.footer-title{color:#fff;font-size:24px;font-weight:700;margin:0;}
footer p.footer-p{color:#ccc;font-size:14px;line-height:25px;}
#section-footer:before, #section-footer:after{content:"";position:absolute;background-image:url(/wp-content/uploads/2023/09/bbg-pattern-f7-40.png);background-repeat:repeat-y;background-size:auto;width:75px;height:-webkit-fill-available;opacity:0.6;}
#section-footer:before{left:0;top:0;}
#section-footer:after{right:0;top:0;}
.et_pb_social_media_follow li a.icon:hover{opacity:0.8;}
#copyright{font-size:14px;}

/* Homepage Admission */
#section-admission{background-color:#0A5088!important;}
#campus{display:none;}
#come-join-us{color:#fff;font-size:22px;font-weight:700;margin:0 0 5px 0;padding:0;}
#our-achievement{padding:0;font-size:14px;font-weight:500;}
#our-achievement i{color:#ECD53C;}

/* Homepage Berita */
#section-berita{background-color:#E5F4FF!important;}
#section-berita:before{content:"";position:absolute;background-image:url(/wp-content/uploads/2023/09/ubbg-batik-270.webp);background-repeat:no-repeat;background-size:contain;display:block;width:270px;height:307px;opacity:0.1;left:0;top:0;}
#section-berita:after{content:"";position:absolute;background-image:url(/wp-content/uploads/2023/09/bbg-pattern-e3.webp);background-repeat:no-repeat;background-size:contain;display:block;height:250px;width:104px;right:0;bottom:0;/*transform:rotate(180deg);-webkit-transform:rotate(180deg);*/}

/* Homepage Tridharma */
#section-tridharma{background-color:#E5F4FF!important;}
#section-tridharma:before{content:"";width:400px;height:120px;background:#D0E4F3;position:absolute;left:0;top:0;}
#section-tridharma:after{content:"";width:400px;height:120px;background:#D0E4F3;position:absolute;right:0;bottom:0;}
#section-tridharma .et_pb_row .et_pb_column{margin-right:0;width:33.3%;}
@media (max-width:600px){
	#section-tridharma .et_pb_row .et_pb_column{width:100%;}
}
@media (max-width:980px){
	#section-tridharma .et_pb_row .et_pb_column{margin:0 0 10px!important;}
}
#section-tridharma .box-image{height:200px;padding-left:80px;padding-right:80px;display:flex;align-items:flex-end;padding-bottom:120px;overflow:hidden;position:relative;z-index:1;}
#section-tridharma .box-image .img{background-size:cover;background-color:#41B2FF;background-blend-mode:multiply;height:100%;width:100%;position:absolute;left:0;top:0;background-position:center center;transition:all .5s ease-in-out;-webkit-transition:all .5s ease-in-out;}
#section-tridharma .box-image .img:hover{background-color:#FFF;transform:scale(1.2);transition:all .5s ease-in-out;-webkit-transition:all .5s ease-in-out;}
#section-tridharma .box-image .box-content{z-index:99;background:#ECD53C;color:#0A528B;padding:0px 8px;position:absolute;left:15px;bottom:15px;}
#section-tridharma .box-image .box-content .caption{font-weight:500;font-size:15px;}

/* Homepage Pengumuman Event */
#section-pengumuman-event:before{content:"";position:absolute;background-image:url(/wp-content/uploads/2023/09/bbg-pattern-e3.webp);background-repeat:no-repeat;background-size:contain;display:block;width:104px;height:250px;transform:rotate(180deg);-webkit-transform:rotate(180deg);}
#section-pengumuman-event:after{content:"";position:absolute;background-image:url(/wp-content/uploads/2023/09/bbg-pattern-e3.webp);background-repeat:no-repeat;background-size:contain;display:block;height:250px;width:104px;right:0;bottom:0;/*transform:rotate(180deg);-webkit-transform:rotate(180deg);*/}
/* #section-pengumuman-event:after{content:"";position:absolute;background-image:url(/wp-content/uploads/2023/09/ubbg-batik-270.webp);background-repeat:no-repeat;background-size:contain;display:block;width:270px;height:307px;opacity:0.1;right:0;bottom:0;transform:rotate(180deg);-webkit-transform:rotate(180deg);} */
#section-pengumuman-event{background-color:#E5F4FF!important;}
@media only screen and (max-width:560px){
	.wpjc-title-left, .wpjan-title-left, .wpjev-title-left {width:100%!important;}
	.wpjc-title-right, .wpjan-title-right, .wpjev-title-right {display:none!important;}
}

/* Homepage Testimoni */
#section-testimoni{background-color:#E5F4FF!important;padding-bottom:30px !important;}
#section-testimoni:before{content:"";width:400px;height:120px;background:#D0E4F3;position:absolute;left:0;top:0;}
#section-testimoni:after{content:"";width:400px;height:120px;background:#D0E4F3;position:absolute;right:0;bottom:0;}

/* Homepage Clipping */
#section-clipping{background-color:#0A5088!important;}
@media (max-width:980px){
	#section-clipping .et_pb_row .et_pb_column{margin-right:3.5%!important;}
}
@media (max-width: 479px){
	#section-clipping .et_pb_row .et_pb_column{
		margin-bottom:10px!important;width:29.666%!important;
	}
}
#section-clipping .cover-clipping{padding:0!important;transform:scale(1);transition:transform 0.3s ease-in-out;}
#section-clipping .cover-clipping:hover{transform:scale(1.05);}

/* Post & Page */
.et_pb_post_content p strong a{font-weight:700!important;}
.et_pb_post_title_0_tb_body .et_pb_title_featured_container img, .et_pb_image_container img, .et_pb_post a img{-webkit-filter: brightness(107%) contrast(107%);}
.et_pb_post .entry-featured-image-url{margin-bottom:0;}
.et_pb_post_content a{font-weight:500;}
.et_pb_blog_grid .et_audio_content, .et_pb_blog_grid .et_main_video_container, .et_pb_blog_grid .et_pb_post .et_pb_slider, .et_pb_image_container{margin:-20px -20px 10px;}
.et_pb_blog_grid .et_pb_post{background:url(/wp-content/uploads/2023/08/sg.png) no-repeat bottom right #FFF;border:1px solid #d8d8d8;opacity:1!important;margin-bottom:20px!important;}
.et_pb_title_meta_container{padding-bottom:10px!important;}
.et_pb_post .entry-title, .not-found-title{font-size:26px!important;padding:0;}
#judul-pos .et_pb_title_featured_container img{-webkit-filter:brightness(107%) contrast(107%);}
#judul-laman .et_pb_title_featured_container img{-webkit-filter:unset;padding-bottom:30px;}
#pos-terbaru .et_pb_post .entry-title{margin:0 -8px;}
.et_pb_post .post-meta{margin-top:5px;}
#pos-terbaru .et_pb_post .post-meta{margin:5px -8px -13px -8px;}

/* Content */
.entry-content ul, .et-l--body ul{padding:0 0 23px 36px;}
.entry-content ol, .et-l--body ol{list-style-position:inherit;padding:0 0 23px 20px;}

/* Ivory Search */
.is-menu {display: inline-block;vertical-align: sub;}
.is-menu a {background:none!important;line-height:1;outline:0;padding:0 10px;}
.is-menu a svg {width:16px;height:auto;}
.is-menu .search-icon-path {fill:#fff;}
.popup-search-close:before, .search-close:before, .popup-search-close:after, .search-close:after {border-left:3px solid #fff;box-shadow:0px 0px 3px #000;}
@media only screen and (max-width: 980px) {
	.is-menu.is-dropdown .search-close {display:none!important;}
}
.mega-menu-horizontal .is-menu {margin-bottom:5px;}
.mega-menu-horizontal .is-menu a {padding:0 0 0 18px;}

/* WP PageNavi */
.wp-pagenavi {text-align:left;}
.wp-pagenavi a {font-weight:700 !important;}
.wp-pagenavi span.pages {float:right;}
.wp-pagenavi a:hover, .wp-pagenavi span.current {font-weight:700!important;background:#ff8200!important;color:#fff!important;padding:5px 10px;}

/* WP Download Manager*/
.w3eden .media-heading a {color:#666;cursor:default;}
.w3eden .media-heading a:hover {color:#666;text-decoration:none;}
@media (max-width: 980px) {
	.w3eden .card h3 {font-size:9pt;}
}
.w3eden .list-group li:nth-child(3), .w3eden .list-group li:nth-child(4), .w3eden .list-group li:nth-child(5), .w3eden .list-group li:nth-child(6), #left-area .post-meta {display:none!important;}

/* WP Socializer */
.wp-socializer.wpsr-share-icons {margin:1.5em 0 1em 0;}

/* Replace Icon Twitter to X-Twitter */
.medsos-wrapper a i.fa-twitter-square:before {content: url(https://bbg.ac.id/wp-content/uploads/2023/09/x-twitter-h10.png) !important;}
.et-social-twitter a.icon:before {content: url(https://bbg.ac.id/wp-content/uploads/2023/09/x-twitter-h16.png);margin-top: 1px;}
.socializer .sr-twitter a, .socializer .sr-twitter a:visited {color: #000;border-color: #000;background-color: #000;}
.wpsr-sm-link[title="Tweet this !"] {background-color:#000 !important;}
.fa-twitter:before {content: url(https://bbg.ac.id/wp-content/uploads/2023/09/x-twitter-h16.png) !important;}
